import phc from "@phc/format";
var PhcFormatter = class {
	serialize(salt, hash, options) {
		return phc.serialize({
			id: options.id,
			version: options.version,
			params: options.params,
			salt,
			hash
		});
	}
	deserialize(phcString) {
		return phc.deserialize(phcString);
	}
};
export { PhcFormatter };
